using Friendica_Mobile.Strings;
using System;
using Xamarin.Forms;

namespace Friendica_Mobile
{
    public static class WebViewEditorHtml
    {
        static string BodyBackgroudColor { get { return (Settings.AppThemeDarkModeEnabled) ? "black" : "white";  } }
        static string ButtonBackgroundColor { get { return (Settings.AppThemeDarkModeEnabled) ? "#333333" : "#CCCCCC"; } }
        static string ButtonTextColor { get { return (Settings.AppThemeDarkModeEnabled) ? "white" : "black"; } }
        static string EditorBackgroundColor { get { return (Settings.AppThemeDarkModeEnabled) ? "lightgray" : "whitesmoke"; } }
        static string BlockquoteBackgroundColor { get { return "gold"; } }
        static string CodeBackgroundColor { get { return "darkgray"; } }
        static string MarginTopEditor 
        { 
            get
            {
                // small offset between buttons and editor field because iOS shows itself a formatting bar which is otherwise covering the buttons
                switch (Device.RuntimePlatform)
                {
                    case Device.iOS:
                    case Device.Android:
                        return "24px";
                    case Device.UWP:
                        return "12px";
                    case Device.macOS:
                    default:
                        return "0px";
                }
            } 
        }
        static string EditorHeight
        {
            get
            {
                // UWP on Windows desktops has more size to display the editor, because webview is higher to avoid scrolling in 
                // color selector which results in losing focus
                switch (Device.RuntimePlatform)
                {
                    case Device.UWP:
                        return "300px";
                    case Device.iOS:
                    case Device.Android:
                    case Device.macOS:
                    default:
                        return "200px";
                }
            }
        }


        public static string CreateEditorHtml()
        {
            string html = "";

            html += "<!doctype html>\n";
            html += "<html>\n";
            html += "<head>\n";
            html += "<meta name=\"viewport\" content=\"width=device-width, user-scalable=yes\">";

            // scripts for editor usage and building
            html += "<script type='text/javascript'>\n";
            // function to return html to app
            html += "function getHtml() { return document.getElementById(\"rte-editbox-0\").innerHTML; }";
            // function to add a link from c# on UWP (workaround as prompt for asking in JavaScript not working on UWP)
            html += "function setLinkUWP(sLnk) { if (sLnk && sLnk !== \"http://\"){ document.execCommand(\"createlink\", false, sLnk); return 'test'; } }";
            html += "(function () { ";
            // function sets formats selected with buttons etc.
            html += "function formatDoc (oDoc, sCmd, sValue) {" +
                        "if (!validateMode(oDoc)) { return; } " +
                        "document.execCommand(sCmd, false, sValue); " +
                        "oDoc.focus();" +
                    "}";
            html += "function validateMode (oDoc) {" +
                        "if (!document.getElementById(\"rte-mode-\" + rId.exec(oDoc.id)[0]).checked) { return true; }" +
                        "alert(\"Uncheck \\u00AB\" + sModeLabel + \"\\u00BB.\");" +
                        "oDoc.focus();" +
                        "return false;" +
                    "}";
            html += "function extractText (oDoc) {" +
                        "if (oDoc.innerText) { return oDoc.innerText; }" +
                        "var oContent = document.createRange();" +
                        "oContent.selectNodeContents(oDoc.firstChild);" +
                        "return oContent.toString();" +
                    "}";
            html += "function setDocMode (oDoc, bToSource) {" +
                        "if (bToSource) {" +
                            "var oContent = document.createTextNode(oDoc.innerHTML), oPre = document.createElement(\"pre\");" +
                            "oDoc.innerHTML = \"\";" +
                            "oDoc.contentEditable = false;" +
                            "oPre.className = \"rte-sourcetext\";" +
                            "oPre.id = \"rte-source-\" + oDoc.id;" +
                            "oPre.onblur = oDoc.onblur;" +
                            "oPre.contentEditable = true;" +
                            "oPre.appendChild(oContent);" +
                            "oDoc.appendChild(oPre);" +
                        "} else {" +
                            "oDoc.innerHTML = extractText(oDoc);" +
                            "oDoc.contentEditable = true;" +
                        "}" +
                        "oDoc.focus();" +
                    "}";
            html += "function menuSelect () {" +
                        "if (this.selectedIndex < 1) { return; }" +
                        "var sMenuGroup = rId.exec(this.id)[0], sCmd = this.id.slice(0, - sMenuGroup.length);" +
                        "formatDoc(aEditors[sMenuGroup], sCmd, this[this.selectedIndex].value);" +
                        "this.selectedIndex = 0;" +
                    "}";
            html += "function buttonClick () {" +
                        "var sBtnGroup = rId.exec(this.id)[0], sCmd = this.id.slice(0, - sBtnGroup.length);" +
                        "customCommands.hasOwnProperty(sCmd) ? customCommands[sCmd](aEditors[sBtnGroup]) : formatDoc(aEditors[sBtnGroup], sCmd, this.alt || false);" +
                    "}";
            html += "function changeMode () {" +
                        "setDocMode(aEditors[rId.exec(this.id)[0]], this.checked);" +
                    "}";
            html += "function updateField () {" +
                        "var sFieldNum = rId.exec(this.id)[0];" +
                        "document.getElementById(\"rte-field-\" + sFieldNum).value = document.getElementById(\"rte-mode-\" + sFieldNum).checked ? extractText(this) : this.innerHTML;" +
                    "}";
            html += "function createMenuItem (sValue, sLabel) {" +
                        "var oNewOpt = document.createElement(\"option\");" +
                        "oNewOpt.value = sValue;" +
                        "oNewOpt.style.color = sValue;" +
                        "oNewOpt.innerHTML = sLabel || \"&#x2588;&#x2588;&nbsp;&nbsp; \" + sValue;" +
                        "return oNewOpt;" +
                    "}";
            html += "function createEditor (oTxtArea) {" +
            	        "var nEditorId = aEditors.length, oParent = document.createElement(\"div\"), oMenuBar = document.createElement(\"div\")," +
            	            "oToolsBar = document.createElement(\"div\"), oEditBox = document.createElement(\"div\")," +
            	            "oModeBox = document.createElement(\"div\"), oModeChB = document.createElement(\"input\")," +
            	            "oModeLbl = document.createElement(\"label\");" +
            	        "oParent.className = \"rich-text-editor\";" +
            	        "oParent.id = oTxtArea.id || \"rich-text-\" + nEditorId;" +
            	        "oMenuBar.className = \"rte-menus\";" +
            	        "oToolsBar.className = \"rte-tools\";" +
            	        "oEditBox.className = \"rte-editbox\";" +
            	        "oEditBox.id = \"rte-editbox-\" + nEditorId;" +
            	        "oEditBox.contentEditable = true;" +
            	        "oEditBox.innerHTML = oTxtArea.value;" +
            	        "aEditors.push(oEditBox);" +
            	        "if (oTxtArea.form) {" +
            	            "var oHiddField = document.createElement(\"input\");" +
            	            "oHiddField.type = \"hidden\";" +
            	            "oHiddField.name = oTxtArea.name;" +
            	            "oHiddField.value = oEditBox.innerHTML;" +
            	            "oHiddField.id = \"rte-field-\" + nEditorId;" +
            	            "oTxtArea.form.appendChild(oHiddField);" +
            	            "oEditBox.onblur = updateField;" +
            	        "}" +
            	        "for (var oMenu, oMenuOpts, vOpt, nMenu = 0; nMenu < oTools.menus.length; nMenu++) {" +
            	            "oMenu = document.createElement(\"select\");" +
            	            "oGroup = document.createElement(\"optgroup\");" +
            	            "oMenu.id = oTools.menus[nMenu].command + nEditorId;" +
            	            "oMenu.onchange = menuSelect;" +
            	            "oGroup.appendChild(createMenuItem(oTools.menus[nMenu].header, oTools.menus[nMenu].header));" +
            	            "oMenuOpts = oTools.menus[nMenu].values;" +
            	            "if (oMenuOpts.constructor === Array) {" +
            	                "for (vOpt = 0; vOpt < oMenuOpts.length; oGroup.appendChild(createMenuItem(oMenuOpts[vOpt++])));" +
            	            "} else {" +
            	                "for (vOpt in oMenuOpts) { oGroup.appendChild(createMenuItem(vOpt, oMenuOpts[vOpt])); }" +
            	            "}" +
            	            "oMenu.selectedIndex = 0;" +
            	            "oMenu.appendChild(oGroup);" +
            	            "oMenuBar.appendChild(document.createTextNode(\" \"));" +
            	            "oMenuBar.appendChild(oMenu);" +
            	        "}" +
            	        "for (var oBtnDef, oButton, nBtn = 0; nBtn < oTools.buttons.length; nBtn++) {" +
            	            "oBtnDef = oTools.buttons[nBtn];" +
            	            "oButton = document.createElement(\"img\");" +
            	            "oButton.className = \"rte-button\";" +
            	            "oButton.id = oBtnDef.command + nEditorId;" +
            	            "oButton.src = oBtnDef.image;" +
            	            "oButton.width = 36;" +
                            "oButton.height = 36;" +
                            "if (oBtnDef.hasOwnProperty(\"value\")) { oButton.alt = oBtnDef.value; }" +
                            "if (oBtnDef.hasOwnProperty(\"displaytext\")) { oButton.alt = oBtnDef.displaytext; }" +
                            "oButton.title = oBtnDef.text;" +
                            "oButton.onclick = buttonClick;" +
                            "oToolsBar.appendChild(oButton);" +
                        "}" +
                        "oModeBox.className = \"rte-switchmode\";" +
                        "oModeChB.type = \"checkbox\";" +
                        "oModeChB.id = \"rte-mode-\" + nEditorId;" +
                        "oModeChB.onchange = changeMode;" +
                        "oModeLbl.setAttribute(\"for\", oModeChB.id);" +
                        "oModeLbl.style.color = \"" + ButtonTextColor + "\";" +
                        "oModeLbl.innerHTML = sModeLabel;" +
                        "oModeBox.appendChild(oModeChB);" +
                        "oModeBox.appendChild(document.createTextNode(\" \"));" +
                        "oModeBox.appendChild(oModeLbl);" +
                        "oParent.appendChild(oMenuBar);" +
                        "oParent.appendChild(oToolsBar);" +
                        "oParent.appendChild(oEditBox);" +
                        "oParent.appendChild(oModeBox);" +
                        "oTxtArea.parentNode.replaceChild(oParent, oTxtArea);" +
                    "}";
            html += "function replaceFields (nFlag) {" +
                        "nReady |= nFlag;" +
                        "if (nReady !== 3) { return; }" +
                        "for (" +
                            "var oField, nItem = 0, aTextareas = Array.prototype.slice.call(document.getElementsByTagName(\"textarea\"), 0);" +
                            "nItem < aTextareas.length;" +
                            "oField = aTextareas[nItem++], oField.className !== \"rich-text-editor\" || createEditor(oField)" +
                        ");" +
                    "}";
            html += "function documentReady () { replaceFields(1); }" +
            	        "var oTools, nReady = 0, sModeLabel = \"" + AppResources.TextEditorShowHtmlCheckbox + "\", aEditors = [], rId = /\\d+$/," +
            	            "customCommands = {" +
            	                "\"createLink\": function (oDoc) {" +
                                    "var sLnk = prompt(\"Write the URL here\", \"http://\");" +
                                    "if (sLnk && sLnk !== \"http://\"){ formatDoc(oDoc, \"createlink\", sLnk); }" +
                                "}" +
                            "};\n\n" +
                        "oTools = JSON.parse('" + CommandsJson() + "');\n" +
                        "replaceFields(2);\n" +
                        "window.addEventListener ? addEventListener(\"load\", documentReady, false) : window.attachEvent ? attachEvent(\"onload\", documentReady) : window.onload = documentReady;\n" +
                    "})();";
            html += "</script>\n";

            // style for the page
            html += "<style type=\"text/css\">\n";
            html += "img.rte-button { cursor: pointer; border: 0; background-color: " + ButtonBackgroundColor + "; }";
            html += "div.rich-text-editor { display: inline-block; padding: 3px; }";
            html += "div.rte-menus select { font-size: 20px; }";
            html += "div.rte-editbox { margin-top: " + MarginTopEditor + "; min-height: " + EditorHeight + "; max-height: " + EditorHeight + "; border: 1px #aaaaaa solid; background-color: " + EditorBackgroundColor + "; padding: 6px; overflow: scroll; }";
            html += "div.rte-editbox p { margin: 0; }";
            html += "div.rte-editbox > pre.rte-sourcetext { padding: 0; margin: 0; }";
            html += "div.rte-switchmode { font-size: 12px; text-align: right; padding: 2px 0 0 2px; }";
            html += "div.rte-switchmode > label { cursor: pointer; }";
            html += ".rich-text-editor, input[type=text] { border: 1px #aaaaaa; }";
            // quotes and code blocks (<pre>) should be highlighted with a different background color
            html += "blockquote { background-color: " + BlockquoteBackgroundColor + "; }";
            html += "pre { background-color: " + CodeBackgroundColor +"; }";
            html += "body { background-color: " + BodyBackgroudColor + "; }";
            html += "select { background-color: " + ButtonBackgroundColor + "; color: " + ButtonTextColor + "; height: 36px; font-size: 20px; }";
            html += "optgroup { font-size: 20px; }";
            html += "#newcomment { width: 100%; height: auto; }";
            html += "</style>\n\n";
            html += "</head>";

            // body of html page
            html += "<body>";
            html += "<form><textarea name=\"comment\" id=\"newcomment\" class=\"rich-text-editor\"></textarea></form>";
            html += "</body>";
            html += "</html>";
            return html;
        }

        private static string CommandsJson()
        {
            var json = "";
            if (Settings.AppThemeDarkModeEnabled)
            {
                json += "{\"menus\":" +
                    "[" +
                    "{\"command\":\"formatblock\",\"header\":\"- " + AppResources.TextEditorFormatHeader + " -\",\"values\":{\"h2\":\"Title 1\",\"h3\":\"Title 2\",\"div\":\"Standard\"}}," +
                    "{\"command\":\"forecolor\",\"header\":\"- " + AppResources.TextEditorColorHeader + " -\",\"values\":{\"black\": \"\",\"gray\":\"\",\"silver\":\"\",\"white\":\"\",\"purple\":\"\",\"navy\":\"\",\"blue\":\"\",\"aqua\":\"\",\"maroon\":\"\",\"red\":\"\",\"orange\":\"\",\"yellow\":\"\",\"green\":\"\",\"olive\":\"\",\"lime\":\"\",\"fuchsia\":\"\"}}," +
                    "{\"command\":\"backcolor\",\"header\":\"- " + AppResources.TextEditorBackgroundHeader + " -\",\"values\":{\"silver\":\"\",\"aqua\":\"\",\"yellow\":\"\",\"lime\":\"\",\"fuchsia\":\"\"}}" +
                    "]," +
                    "\"buttons\":" +
                    "[" +
                    "{\"text\":\"" + AppResources.TextEditorBoldTooltip + "\",\"command\":\"bold\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorItalicTooltip + "\",\"command\":\"italic\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorUnderlineTooltip + "\",\"command\":\"underline\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorNumberedListTooltip + "\",\"command\":\"insertorderedlist\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorDottedListTooltip + "\",\"command\":\"insertunorderedlist\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorQuoteTooltip + "\",\"command\":\"formatblock\",\"value\":\"blockquote\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorCodeTooltip + "\",\"command\":\"formatblock\",\"value\":\"pre\",\"image\":\"data:image/png;base64,\"},";
                // hyperlink button is not working on UWP (not showing prompt to enter link)
                if (Device.RuntimePlatform != Device.UWP)
                {
                    json += "{\"text\":\"" + AppResources.buttonLinkCaption_Text + "\",\"command\":\"createLink\",\"image\":\"data:image/png;base64,\"},";
                }
                json += "{\"text\":\"" + AppResources.TextEditorRemoveFormattingTooltip + "\",\"command\":\"removeFormat\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorUndoTooltip + "\",\"command\":\"undo\",\"image\":\"data:image/png;base64,\"}" +
                    "]}";
            }
            else
            {
                json += "{\"menus\":" +
                    "[" +
                    "{\"command\":\"formatblock\",\"header\":\"- " + AppResources.TextEditorFormatHeader + " -\",\"values\":{\"h2\":\"Title 1\",\"h3\":\"Title 2\",\"div\":\"Standard\"}}," +
                    "{\"command\":\"forecolor\",\"header\":\"- " + AppResources.TextEditorColorHeader + " -\",\"values\":{\"black\": \"\",\"gray\":\"\",\"silver\":\"\",\"white\":\"\",\"purple\":\"\",\"navy\":\"\",\"blue\":\"\",\"aqua\":\"\",\"maroon\":\"\",\"red\":\"\",\"orange\":\"\",\"yellow\":\"\",\"green\":\"\",\"olive\":\"\",\"lime\":\"\",\"fuchsia\":\"\"}}," +
                    "{\"command\":\"backcolor\",\"header\":\"- " + AppResources.TextEditorBackgroundHeader + " -\",\"values\":{\"silver\":\"\",\"aqua\":\"\",\"yellow\":\"\",\"lime\":\"\",\"fuchsia\":\"\"}}" +
                    "]," +
                    "\"buttons\":" +
                    "[" +
                    "{\"text\":\"" + AppResources.TextEditorBoldTooltip + "\",\"command\":\"bold\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorItalicTooltip + "\",\"command\":\"italic\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorUnderlineTooltip + "\",\"command\":\"underline\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorNumberedListTooltip + "\",\"command\":\"insertorderedlist\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorDottedListTooltip + "\",\"command\":\"insertunorderedlist\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorQuoteTooltip + "\",\"command\":\"formatblock\",\"value\":\"blockquote\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorCodeTooltip + "\",\"command\":\"formatblock\",\"value\":\"pre\",\"image\":\"data:image/png;base64,\"},";
                if (Device.RuntimePlatform != Device.UWP)
                {
                    json += "{\"text\":\"" + AppResources.buttonLinkCaption_Text + "\",\"command\":\"createLink\",\"image\":\"data:image/png;base64,\"},";
                }
                json += "{\"text\":\"" + AppResources.TextEditorRemoveFormattingTooltip + "\",\"command\":\"removeFormat\",\"image\":\"data:image/png;base64,\"}," +
                    "{\"text\":\"" + AppResources.TextEditorUndoTooltip + "\",\"command\":\"undo\",\"image\":\"data:image/png;base64,\"}" +
                    "]}";
            }
            return json;
        }
    }
}
